import 'dart:math';
import 'dart:typed_data';
import 'package:flutter/material.dart';
import 'package:flutter/widgets.dart';
import 'src/header/header.dart';
import 'dart:convert';
import 'dart:async';

/// 金色校园Header
class PhoenixHeader extends RefreshHeader {
  PhoenixHeader({
    @required GlobalKey<RefreshHeaderState> key,
  }) : super(key: key, refreshHeight: 80.0);

  @override
  PhoenixHeaderState createState() => PhoenixHeaderState();
}

class PhoenixHeaderState extends RefreshHeaderState<PhoenixHeader> {
  // 图片资源
  String _buildingsBase64 =
      "";
  String _sunBase64 =
      "";
  String _skyBase64 =
      "";
  Uint8List _buildingsBytes;
  Uint8List _sunBytes;
  Uint8List _skyBytes;

  // 太阳旋转值
  double _sunRotateValue;
  // 旋转计时器
  Timer _sunRotateTimer;
  // 是否旋转太阳
  bool _isRotateSun;

  // 初始化
  @override
  void initState() {
    super.initState();
    _buildingsBytes = base64Decode(_buildingsBase64);
    _sunBytes = base64Decode(_sunBase64);
    _skyBytes = base64Decode(_skyBase64);
    _sunRotateValue = widget.refreshHeight;
    _isRotateSun = false;
  }

  // 正在刷新
  @override
  void onRefreshing() {
    super.onRefreshing();
    _isRotateSun = true;
    _sunRotateValue = widget.refreshHeight;
    rotateSun();
  }

  // 旋转太阳
  void rotateSun() {
    _sunRotateTimer = Timer(Duration(milliseconds: 10), () {
      setState(() {
        _sunRotateValue += 2;
        rotateSun();
      });
    });
  }

  // 刷新结束
  @override
  void onRefreshEnd() {
    super.onRefreshEnd();
    _isRotateSun = false;
    if (_sunRotateTimer != null) {
      _sunRotateTimer.cancel();
    }
  }

  // 高度更新
  @override
  void updateHeight(double newHeight) {
    super.updateHeight(newHeight);
  }

  @override
  void dispose() {
    super.dispose();
    if (_sunRotateTimer != null) {
      _sunRotateTimer.cancel();
    }
  }

  @override
  Widget build(BuildContext context) {
    return Container(
      width: double.infinity,
      height: this.height,
      color: Colors.blue,
      child: Stack(
        children: <Widget>[
          new SingleChildScrollView(
            physics: NeverScrollableScrollPhysics(),
            child: Container(
              width: double.infinity,
              height: widget.refreshHeight,
              child: Image.memory(
                _skyBytes,
                fit: BoxFit.cover,
              ),
            ),
          ),
          new SingleChildScrollView(
            physics: NeverScrollableScrollPhysics(),
            child: Container(
              margin: EdgeInsets.only(top: 15.0),
              width: double.infinity,
              height: this.height < widget.refreshHeight
                  ? widget.refreshHeight
                  : this.height,
              child: Image.memory(
                _buildingsBytes,
                fit: BoxFit.fitHeight,
              ),
            ),
          ),
          new SingleChildScrollView(
            physics: NeverScrollableScrollPhysics(),
            child: Container(
              margin: EdgeInsets.only(
                  top: this.height < widget.refreshHeight - 20.0
                      ? widget.refreshHeight / 2 - this.height / 2
                      : 10.0),
              width: double.infinity,
              height: widget.refreshHeight,
              child: Center(
                child: Container(
                    height: widget.refreshHeight,
                    width: 110.0,
                    child: Align(
                      alignment: Alignment.topLeft,
                      child: Transform.rotate(
                        child: Container(
                          height: 30.0,
                          width: 30.0,
                          child: Image.memory(
                            _sunBytes,
                            fit: BoxFit.fill,
                          ),
                        ),
                        angle: (_isRotateSun ? _sunRotateValue : this.height) /
                            8 /
                            pi,
                      ),
                    )),
              ),
            ),
          ),
        ],
      ),
    );
  }
}
