import 'dart:typed_data';
import 'package:flutter/material.dart';
import 'package:flutter/widgets.dart';
import 'src/footer/footer.dart';
import 'dart:convert';
import 'dart:async';

/// 冲上云霄Footer
class TaurusFooter extends RefreshFooter {
  TaurusFooter({
    @required GlobalKey<RefreshFooterState> key,
  }) : super(key: key, loadHeight: 90.0);

  @override
  TaurusFooterState createState() => TaurusFooterState();
}

class TaurusFooterState extends RefreshFooterState<TaurusFooter> {
  // 图片资源
  String _airplaneBase64 =
      "";
  String _cloudsCenterBase64 =
      "";
  String _cloudsLeftBase64 =
      "";
  String _cloudsRightBase64 =
      "";

  Uint8List _airplaneBytes;
  Uint8List _cloudsCenterBytes;
  Uint8List _cloudsLeftBytes;
  Uint8List _cloudsRightBytes;

  // 云朵漂浮值
  int _cloudFloatValue;
  // 漂浮计时器
  Timer _cloudFloatTimer;
  // 是否漂浮
  bool _isCloudFloat;
  // 加载结束
  bool _isLoaded;

  // 初始化
  @override
  void initState() {
    super.initState();
    _airplaneBytes = base64Decode(_airplaneBase64);
    _cloudsCenterBytes = base64Decode(_cloudsCenterBase64);
    _cloudsLeftBytes = base64Decode(_cloudsLeftBase64);
    _cloudsRightBytes = base64Decode(_cloudsRightBase64);
    _cloudFloatValue = 0;
    _isLoaded = false;
    _isCloudFloat = false;
  }

  // 云朵漂浮
  void _cloudFloat() {
    if (!_isCloudFloat &&
        _cloudFloatValue ~/ 20 % 2 == 0 &&
        _cloudFloatValue % 20 == 0) {
      _cloudFloatValue = 0;
      _cloudFloatTimer.cancel();
      return;
    }
    _cloudFloatTimer = Timer(Duration(milliseconds: 40), () {
      setState(() {
        _cloudFloatValue++;
        _cloudFloat();
      });
    });
  }

  // 正在加载
  @override
  void onLoading() {
    super.onLoading();
    _cloudFloatValue = 0;
    _isCloudFloat = true;
    _cloudFloat();
  }

  // 加载完成
  @override
  void onLoaded() {
    super.onLoaded();
    _isCloudFloat = false;
    _isLoaded = true;
  }

  @override
  void onNoMore() {
    super.onNoMore();
    _isCloudFloat = false;
    _isLoaded = true;
  }

  // 刷新结束
  @override
  void onLoadStart() {
    super.onLoadStart();
    _isLoaded = false;
  }

  // 高度更新
  @override
  void updateHeight(double newHeight) {
    super.updateHeight(newHeight);
  }

  @override
  void dispose() {
    super.dispose();
    if (_cloudFloatTimer != null) {
      _cloudFloatTimer.cancel();
    }
  }

  @override
  Widget build(BuildContext context) {
    // 计算云朵漂浮系数
    double _cloudFloatCoefficient;
    if (_cloudFloatValue == 0) {
      _cloudFloatCoefficient = 0;
    } else {
      if (_cloudFloatValue ~/ 20 % 2 == 0) {
        _cloudFloatCoefficient = _cloudFloatValue % 20 / 10;
      } else {
        _cloudFloatCoefficient = 2 - _cloudFloatValue % 20 / 10;
      }
    }
    // 中间云朵大小
    double centerCloudHeight;
    if (this.height > widget.loadHeight) {
      centerCloudHeight =
          38.0 + (this.height - widget.loadHeight) / 5 - _cloudFloatCoefficient;
    } else {
      centerCloudHeight = 38.0 - _cloudFloatCoefficient;
    }
    // 飞机位置
    double airplaneBottom;
    double airplaneLeft;
    if (_isLoaded) {
      airplaneBottom = widget.loadHeight / 2 +
          (widget.loadHeight - this.height) * 22.5 / widget.loadHeight * 5 +
          _cloudFloatCoefficient;
      airplaneLeft = 122.5 +
          (widget.loadHeight - this.height) * 122.5 / widget.loadHeight * 3;
    } else {
      airplaneBottom = this.height < widget.loadHeight
          ? this.height / 2 + _cloudFloatCoefficient
          : widget.loadHeight / 2 + _cloudFloatCoefficient;
      airplaneLeft = this.height < widget.loadHeight
          ? this.height * 122.5 / widget.loadHeight
          : 122.5;
    }
    return Container(
      width: double.infinity,
      height: this.height,
      color: Colors.blue,
      child: Stack(
        children: <Widget>[
          new SingleChildScrollView(
            physics: NeverScrollableScrollPhysics(),
            child: Container(
                width: double.infinity,
                height: this.height < 35.0
                    ? 38.0 + _cloudFloatCoefficient
                    : this.height + 3.0 + _cloudFloatCoefficient,
                child: Align(
                  alignment: Alignment.bottomLeft,
                  child: Container(
                    height: 38.0 + _cloudFloatCoefficient,
                    width: 120.0,
                    child: Image.memory(
                      _cloudsLeftBytes,
                      fit: BoxFit.fitHeight,
                    ),
                  ),
                )),
          ),
          new SingleChildScrollView(
            physics: NeverScrollableScrollPhysics(),
            child: Container(
                width: double.infinity,
                height: this.height < 35.0
                    ? 38.0 + _cloudFloatCoefficient
                    : this.height + 3.0 + _cloudFloatCoefficient,
                child: Align(
                  alignment: Alignment.bottomRight,
                  child: Container(
                    height: 38.0 + _cloudFloatCoefficient,
                    width: 120.0,
                    child: Image.memory(
                      _cloudsRightBytes,
                      fit: BoxFit.fitHeight,
                    ),
                  ),
                )),
          ),
          new SingleChildScrollView(
            physics: NeverScrollableScrollPhysics(),
            child: Container(
              width: double.infinity,
              height: widget.loadHeight,
              child: Center(
                child: Container(
                    height: widget.loadHeight,
                    width: 300.0,
                    child: Stack(
                      children: <Widget>[
                        Positioned(
                          bottom: airplaneBottom,
                          left: airplaneLeft,
                          child: Container(
                            height: 17.5,
                            width: 55.0,
                            child: Image.memory(
                              _airplaneBytes,
                              fit: BoxFit.fill,
                            ),
                          ),
                        )
                      ],
                    )),
              ),
            ),
          ),
          new SingleChildScrollView(
            physics: NeverScrollableScrollPhysics(),
            child: Container(
                width: double.infinity,
                height: this.height < 35.0
                    ? 38.0 - _cloudFloatCoefficient
                    : this.height + 3.0 - _cloudFloatCoefficient,
                child: Align(
                  alignment: Alignment.bottomCenter,
                  child: Container(
                    height: centerCloudHeight,
                    width: double.infinity,
                    child: Image.memory(
                      _cloudsCenterBytes,
                      fit: BoxFit.fitHeight,
                    ),
                  ),
                )),
          ),
        ],
      ),
    );
  }
}
